mapper.select:
SELECT DOC_ID , RND_ID , DOC_NM , DIS_CLASS_DTL , DOC_CLASS_DTL , DOC_HEADER , LINK_URL FROM BDSP.T_DOC_MST WHERE 1=1 AND DOC_CLASS_DTL = 'DC002'
AND DIS_CLASS_DTL = #{disClassDtl}
ORDER BY DOC_ID

SELECT DI.DOC_ID , DI.RND_ID , DI.INQ_NO , DI.INQ_SHRINKING , DI.INQ_CONTENT , DI.INQ_TYPE_CD , DI.SUBJECT_ALLOT_SCORE , DI.MULTI_CHOICE1 , DI.ALLOT_SCORE1 , DI.MULTI_CHOICE2 , DI.ALLOT_SCORE2 , DI.MULTI_CHOICE3 , DI.ALLOT_SCORE3 , DI.MULTI_CHOICE4 , DI.ALLOT_SCORE4 , DI.MULTI_CHOICE5 , DI.ALLOT_SCORE5 , DI.MULTI_CHOICE6 , DI.ALLOT_SCORE6 , DI.MULTI_CHOICE7 , DI.ALLOT_SCORE7 , DI.MULTI_CHOICE8 , DI.ALLOT_SCORE8 , DI.MULTI_CHOICE9 , DI.ALLOT_SCORE9 , DI.MULTI_CHOICE10 , DI.ALLOT_SCORE10 , DI.MULTI_CHOICE11 , DI.ALLOT_SCORE11 , DI.MULTI_CHOICE12 , DI.ALLOT_SCORE12 , DI.MULTI_CHOICE13 , DI.ALLOT_SCORE13 , DI.MULTI_CHOICE14 , DI.ALLOT_SCORE14 , DI.MULTI_CHOICE15 , DI.ALLOT_SCORE15 , DI.MULTI_CHOICE16 , DI.ALLOT_SCORE16 , DI.MULTI_CHOICE17 , DI.ALLOT_SCORE17 , DI.MULTI_CHOICE18 , DI.ALLOT_SCORE18 , DI.MULTI_CHOICE19 , DI.ALLOT_SCORE19 , DI.MULTI_CHOICE20 , DI.ALLOT_SCORE20 , DI.MULTI_CHOICE21 , DI.ALLOT_SCORE21 , DI.MULTI_CHOICE22 , DI.ALLOT_SCORE22 , DI.MULTI_CHOICE23 , DI.ALLOT_SCORE23 , DI.MULTI_CHOICE24 , DI.ALLOT_SCORE24 , DI.MULTI_CHOICE25 , DI.ALLOT_SCORE25 , DI.MULTI_CHOICE26 , DI.ALLOT_SCORE26 , DI.MULTI_CHOICE27 , DI.ALLOT_SCORE27 , DI.MULTI_CHOICE28 , DI.ALLOT_SCORE28 FROM BDSP.T_DOC_INQUIRY DI JOIN BDSP.T_DOC_MST DM ON DI.DOC_ID = DM.DOC_ID AND DM.DOC_CLASS_DTL = 'DC002' AND DM.DIS_CLASS_DTL = 'DS001' WHERE 1=1
AND DM.DOC_ID = #{docId, jdbcType=VARCHAR}
AND DM.DIS_CLASS_DTL = #{disClassDtl}
ORDER BY DI.INQ_NO * 1

SELECT COUNT(*) FROM ( SELECT A.TARGET_ID FROM( SELECT TM.TARGET_ID FROM T_TARGET_MST TM JOIN T_PERFORM_MST PM ON TM.TARGET_ID = PM.TARGET_ID AND TM.RND_ID = PM.RND_ID AND TM.DIS_CLASS_DTL = PM.DIS_CLASS_DTL LEFT JOIN T_PERFORM_TERM PT ON PM.PERFORM_CNT = PT.PERFORM_CNT LEFT JOIN T_CODE_DTL CD ON TM.PROTOCOL = CD.CLASS_CD_DTL WHERE NOT ((TM.PROTOCOL = 'PTC03' AND PM.PERFORM_CNT = 2) OR (TM.PROTOCOL = 'PTC03' AND PM.PERFORM_CNT = 3) OR (TM.PROTOCOL = 'PTC03' AND PM.PERFORM_CNT = 4)) AND NOT PM.STATUS = '0004'
AND TM.TARGET_ID = #{targetId}
AND TM.SEX = #{sex}
AND REPLACE(CD.CLASS_CD_DTL_NM," ","") IN (${protocol})
AND INSTR(PM.PERFORM_CNT, '${performCnt}')
) A GROUP BY A.TARGET_ID ) B
